import { View , Text , Platform, Linking } from 'react-native';
import { StyleSheet } from 'react-native-web';
import { FontAwesome } from '@expo/vector-icons';


export default function FastingPrayer() {

    const abergavany = 'Llanddewi Rhydderch ,Abergavenny, NP79TR';
    const Pontypridd = 'Taff Meadow, Pontypridd, CF371DB';
    const newport = 'Graig Community Centre, Newport, NP108HZ';
    const swansea = 'Aenon Baptist Church, Swansea, SA67AG';
    const cardiff = 'Fairwater Presbyterian Church, St Fagans Road , Cardiff , CF53AL';


    const openMaps = (address) => {
        const scheme = Platform.select({ ios: 'maps:0,0?q=', android: 'geo:0,0?q=' });
        const label = address;
        const url = Platform.select({
          ios: `${scheme}${label}`,
          android: `${scheme}(${label})`
        })
        Linking.openURL(url);
    }

    return (
        <View style={styles.container}>
            <Text style={styles.title}>Fasting Prayer Schedule</Text>
            <Text style={styles.day}>October 31, Nov 1, Nov 2 - Evening</Text>
            <Text style={styles.addressName} onPress={() => openMaps('NP79TR')}>{abergavany}{' '}
      <FontAwesome style={styles.callIcon} name='location-arrow'></FontAwesome>
      </Text>
      <Text style={styles.day}>Nov 3, Nov 4, Nov 5 - Evening</Text>
            <Text style={styles.addressName} onPress={() => openMaps(Pontypridd)}>{Pontypridd}{' '}
      <FontAwesome style={styles.callIcon} name='location-arrow'></FontAwesome>
      </Text>
      <Text style={styles.day}>Nov 6 - Normal Sunday Service</Text>
            <Text style={styles.addressName} onPress={() => openMaps(newport)}>{newport}{' '}
      <FontAwesome style={styles.callIcon} name='location-arrow'></FontAwesome>
      </Text>
      <Text style={styles.day}>Nov 7, Nov 8, Nov 9 - Evening</Text>
            <Text style={styles.addressName} onPress={() => openMaps(swansea)}>{swansea}{' '}
      <FontAwesome style={styles.callIcon} name='location-arrow'></FontAwesome>
      </Text>
      <Text style={styles.day}>Nov 10, Nov 11, Nov 12 - Evening</Text>
            <Text style={styles.addressName} onPress={() => openMaps(cardiff)}>{cardiff}{' '}
      <FontAwesome style={styles.callIcon} name='location-arrow'></FontAwesome>
      </Text>
      <Text style={styles.day}>Nov 13 - Normal Sunday Service</Text>
            <Text style={styles.addressName} onPress={() => openMaps(newport)}>{newport}{' '}
      <FontAwesome style={styles.callIcon} name='location-arrow'></FontAwesome>
      </Text>
      <Text style={styles.day}>Nov 14 - Nov 20 Morning & Evening </Text>
            <Text style={styles.addressName} onPress={() => openMaps(newport)}>{newport}{' '}
      <FontAwesome style={styles.callIcon} name='location-arrow'></FontAwesome>
      </Text>
            </View>
    )

}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        justifyContent: 'flex-start',
    },
    title: {
        fontSize: 20,
        textDecorationLine: 'underline',
        alignSelf: 'center',
        marginBottom: 30,
    }, 
    addressName: {
        color: 'blue',
        textDecorationLine: 'underline',
        alignSelf: 'center',
        marginBottom: 30
    },
    callIcon: {
        color: 'blue',
        fontSize: 20,
        marginLeft: 5,
    },
    day : {
        fontSize: 20,
        alignSelf: 'center',
        marginBottom: 10
    }
});
