import { ScrollView, StyleSheet, TextInput, TouchableOpacity , ImageBackground } from 'react-native';
import { Text, View } from '../components/Themed';
import { useState } from 'react';

export default function Login({ setShowContacts }) {


  const image = require('../assets/images/group.jpg')
  let contactsArray = require('../constants/Contacts.json');

  const password = 'tpc'
  const [passwordText, setPasswordText] = useState('')
  const [phoneNumber, setPhoneNumber] = useState('')
  const [postCode, setPostCode] = useState('')

  const handleLoginClick = () => {

    contactsArray.map(item => {
      if (((item.PhoneNumber == phoneNumber) || (item.SpousePhNo == phoneNumber)) && (item.postcode == postCode) && (passwordText == password)) {
        setShowContacts(true)
      }
    })
  }

  return (
    <View style={styles.container}>
      <ScrollView>
      <ImageBackground source={image} style={{
    width: "100%",
    height: 140,
    backgroundColor:'#000',
    marginTop:10,
    position: 'absolute',
  top:0
  }}
  ></ImageBackground>
      <TextInput
        style={styles.phonenumber}
        placeholder="Enter Your Phone Number Registered With Church..."
        keyboardType="default"
        onChangeText={text => { setPhoneNumber(text) }}
      /><TextInput
        style={styles.postCode}
        placeholder="Enter Your Postcode..."
        keyboardType="default"
        onChangeText={text => { setPostCode(text) }}
      />
      <TextInput
        style={styles.password}
        placeholder="Enter Your Password..."
        keyboardType="default"
        onChangeText={text => { setPasswordText(text) }}
        secureTextEntry={true}
      />

      <TouchableOpacity
        style={styles.button}
        onPress={() => handleLoginClick()}
      >
        <Text style={styles.text}>Login</Text>
      </TouchableOpacity>
    </ScrollView>
    </View>
  )
}

const styles = StyleSheet.create({
  container: {
   flex : 1
  },
  button: {
    alignItems: 'center',
    justifyContent: 'center',
    paddingVertical: 17,
    paddingHorizontal: 55,
    borderRadius: 4,
    backgroundColor: '#eb5d0c',
    bottom : 0
  },
  text: {
    fontSize: 16,
    lineHeight: 21,
    fontWeight: 'bold',
    letterSpacing: 0.25,
    color: 'white',
  },
  password: {
    height: 50,
    width: 350,
    borderWidth: 0.2,
    marginTop: 70,
    borderRadius: 20,
    padding: 10,
    marginBottom: 40,
    top: 10,
  },
  phoneNumber: {
    height: 50,
    width: 350,
    borderWidth: 0.2,
    marginTop: 70,
    borderRadius: 20,
    padding: 10,
    marginBottom: 40,
    top: 10,
  },
  postCode: {
    height: 50,
    width: 350,
    borderWidth: 0.2,
    marginTop: 70,
    borderRadius: 20,
    padding: 10,
    marginBottom: 40,
    top: 10,
  },
  tinyLogo : {
    width : '90%',
    height : '50%',
    marginTop : 20,
    display : 'flex'
  }
})
