import { useNavigation } from '@react-navigation/native';
import { NativeStackNavigationProp } from '@react-navigation/native-stack';
import { fetchUpdateAsync } from 'expo-updates';
import { useEffect, useState } from 'react';
import { Alert, ScrollView, StyleSheet, TextInput, Image } from 'react-native';

import EditScreenInfo from '../components/EditScreenInfo';
import { Text, View } from '../components/Themed';
import { RootTabScreenProps } from '../types';
//import ModalScreen from './ModalScreen';

const TabOneScreen = ({  }: RootTabScreenProps<'TabOne'>) => {

  const navigation = useNavigation<NativeStackNavigationProp<any>>();


    const fetchData = async () => {
      const response = await fetch(
        'https://api.jsonbin.io/b/623f0b0806182767438015a2'
      );
      const json =  await response.json();
     // console.log(json)

    };
    fetchData()







  let contactsArray = require('../constants/Contacts.json');
     const[filteredArray,setfilteredArray] = useState(contactsArray);

      const handleSearch = (text) => {
          setfilteredArray(contactsArray.filter(i => i.Name.toLowerCase().includes(text)))
     }

  const handleContactClick = (name,address,Photo,phoneNo,spouseName,spouseNo,email,kids) => {
    navigation.navigate("Modal", {contactname : name,address : address, photo : Photo, phoneNo : phoneNo,spouseName : spouseName,
    spouseNo : spouseNo,email : email,kids : kids})
  }
  return (
    <ScrollView style={styles.scrollView}>
      <View style={styles.container}>

        <TextInput
          style={styles.input}
          placeholder="Search By Name....."
          keyboardType="name-phone-pad"
          onChangeText={text => { handleSearch(text) }}

        />
        {filteredArray.map((item,i) => {
          return (
            <View style={styles.contactview}
            key={i}
            >

              <Image
               key={i+1}
                style={styles.tinyLogo}
                source={{
                  uri: item.Photo,
                }}
              />
              <Text  key={i+2} style={styles.contacts}  onPress={() => handleContactClick(item.Name,item.Address,item.Photo,item.PhoneNumber,
                item.SpouseName,item.SpousePhNo,item.email,item.kids)}>
                {item.Name}
              </Text>
            </View>

          )


        }
        )}
      </View>
    </ScrollView>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
  },
  scrollView: {
  },
  contacts: {
    borderWidth: 0.16,
    marginTop: -55,
    height:100,
    width: 400,
    paddingTop: 26,
    fontWeight : '500',
    fontSize : 15,
    paddingLeft: 110
  },
  input: {
    height: 50,
    width: 350,
    borderWidth: 0.2,
    marginTop: 20,
    borderRadius: 20,
    padding: 10,
    marginBottom: 40
  },
  tinyLogo: {
    width: 50,
    height: 50,
    borderRadius : 30,
    marginLeft : 40,
    marginBottom: -5,
  },
  contactview: {
  height : 70,
  marginTop: 0,

  }
});

export default TabOneScreen;
