import { useNavigation } from '@react-navigation/native';
import { NativeStackNavigationProp } from '@react-navigation/native-stack';
import { useState } from 'react';
import { Alert, ScrollView, StyleSheet, TextInput, Image  } from 'react-native';
import FastImage from 'react-native-fast-image';

import EditScreenInfo from '../components/EditScreenInfo';
import { Text, View } from '../components/Themed';
import { RootTabScreenProps } from '../types';
import Login from './Login';

const TabOneScreen = ({  }: RootTabScreenProps<'TabOne'>) => {

  const[showContacts, setShowContacts] = useState(false)

  const navigation = useNavigation<NativeStackNavigationProp<any>>();

  let contactsArray = require('../constants/Contacts.json');
     const[filteredArray,setfilteredArray] = useState(contactsArray);

      const handleSearch = (text) => {
          setfilteredArray(contactsArray.filter(i => i.Name.toLowerCase().includes(text) || i.SpouseName.toLowerCase().includes(text)))
     }

  const handleContactClick = (name,address,Photo,phoneNo,spouseName,spouseNo,email,kids) => {
    navigation.navigate("Modal", {contactname : name,address : address, photo : Photo, phoneNo : phoneNo,spouseName : spouseName,
    spouseNo : spouseNo,email : email,kids : kids})
  }
  return (

      <><View style={styles.container}>
        {!showContacts && <Text style={styles.info}>Note : Please enter the password given by church to view contacts</Text>}
      {!showContacts && <Login setShowContacts={setShowContacts}></Login>}
    </View>

    <ScrollView style={styles.scrollView}>
        {showContacts && <View style={styles.container}>

          <TextInput
            style={styles.input}
            placeholder="Search By Name....."
            keyboardType="name-phone-pad"
            onChangeText={text => { handleSearch(text) } } />
          {filteredArray.map((item, i) => {
            return (
              <View style={styles.contactview}
                key={i}
              >

              <FastImage
              key={i + 1}
              style={styles.tinyLogo}
                source={{
                  uri: item.Photow,
                  cache: FastImage.cacheControl.cacheOnly
                }}
              />

                {/* <Image
                  key={i + 1}
                  style={styles.tinyLogo}
                  source={{
                    uri: item.Photo,
                  }} /> */}
                <Text key={i + 2} style={styles.contacts} onPress={() => handleContactClick(item.Name, item.Address, item.Photo, item.PhoneNumber,
                  item.SpouseName, item.SpousePhNo, item.email, item.kids)}>
                  {item.Name}
                </Text>
              </View>

            );


          }
          )}
        </View>}
      </ScrollView></>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
    backgroundColor : 'white'
  },
  info : {
top : 390,
fontSize : 10,
color : 'red',
marginLeft: -45
  },
  scrollView: {
  },
  contacts: {
    borderWidth: 0.16,
    marginTop: -55,
    height:100,
    width: 400,
    paddingTop: 26,
    fontWeight : '500',
    fontSize : 15,
    paddingLeft: 110
  },
  input: {
    height: 50,
    width: 350,
    borderWidth: 0.2,
    marginTop: 20,
    borderRadius: 20,
    padding: 10,
    marginBottom: 40
  },
  tinyLogo: {
    width: 50,
    height: 50,
    borderRadius : 30,
    marginLeft : 40,
    marginBottom: -5,
  },
  contactview: {
  height : 70,
  marginTop: 0,
  }
});

export default TabOneScreen;
