//import ModalScreen from './ModalScreen';

const TabThreeScreen = ({  }: RootTabScreenProps<'TabOne'>) => {
    const image = { uri: "https://drive.google.com/uc?export=view&id=1UsYhWJ58OkaO1dq7GtCPlgkfZ7KfFIiw" };
    const [isLoading, setLoading] = useState(true);
    const [data, setData] = useState([]);
    useEffect(() => {
        fetch('https://beta.ourmanna.com/api/v1.json')
          .then((response) => response.json())
          .then((json) => setData(json))
          .catch((error) => console.error(error))
          .finally(() => setLoading(false));
      }, []);

  return (
 <View style={styles.container}>
<Image
                  key={i + 1}
                  style={styles.image}
                  source={{
                    uri: "https://drive.google.com/uc?export=view&id=1UsYhWJ58OkaO1dq7GtCPlgkfZ7KfFIiw",
                  }} />
     {/* <ImageBackground source={image} resizeMode="cover" style={styles.image}></ImageBackground> */}
      {isLoading ? <Text>Loading...</Text> : 
      ( <View style={{ flex: 1, flexDirection: 'column' , justifyContent: 'center'}}>
          <Text style={styles.reference}>{data.verse.details.reference}</Text>
          <Text style={styles.verse}>"{data.verse.details.text}"</Text>
        </View>
      )}
    </View>


  )
}

