import { StatusBar } from 'expo-status-bar';
import { Platform, StyleSheet ,Image,Linking,TouchableOpacity} from 'react-native';

import EditScreenInfo from '../components/EditScreenInfo';
import { Text, View } from '../components/Themed';
import TabOneScreen from './TabOneScreen';

export default function ModalScreen({ navigation , route }) {

  const { contactname , address , photo , phoneNo , spouseName , spouseNo , email , kids  } = route.params;
  const goBackHandler = () => {
    navigation.navigate('TabOne')
  }

  const makeCall = (phoneNo) => {
    const url = 'telprompt:'+phoneNo;
    Linking.openURL(url);
  }
  return (
    <View style={styles.container}>
      <Image
      style={styles.tinyLogo}
      source={{
        uri: photo,
      }}
      />
      <View style={styles.detailsContainer}>
      <Text style={styles.mainName}>{contactname}</Text>
      <Text style={styles.contactName}>Spouse Name: {spouseName}</Text>
      <Text style={styles.contactName}>Kids Name: {kids}</Text>

      <Text style={styles.addressName}>Address: {address}</Text>
      <TouchableOpacity>
      <Text style={styles.numberLabel}>Phone No:
      <Text style={styles.contactNumber}  onPress={() => makeCall(phoneNo)}>  {phoneNo}</Text></Text>
      </TouchableOpacity>

      <Text style={styles.contactName}>Spouse No: {spouseNo}</Text>
      <Text style={styles.contactName}>Email: {email}</Text>

      </View>
       <Text style={styles.goback} onPress={() => goBackHandler()}>Go Back</Text>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
    backgroundColor : 'white'
  },
  title: {
    fontSize: 20,
    fontWeight: 'bold',
  },
  separator: {
    marginVertical: 30,
    height: 1,
    width: '80%',
  },
  tinyLogo: {
    width: 200,
    height: 200,
    marginTop:-50
  },
  goback: {
    color : 'blue',
    marginTop : 50
  },
  detailsContainer: {
     marginTop: 60,
     marginLeft: 40
  },
  contactName: {
    textAlign: 'left',
    fontWeight : '600',
    fontSize : 15,
    marginBottom: 5
  },
  addressName: {
    textAlign: 'left',
    fontWeight : '600',
    fontSize : 15,
    marginBottom: 5,
    marginTop: 10
  },
  contactNumber: {
    color: 'blue',
    fontWeight : '600'
  },
  numberLabel : {
    fontWeight : '600',
    marginBottom: 5

  },
  mainName : {
    textAlign: 'left',
    fontWeight : '800',
    fontSize : 17,
    marginBottom: 10
  }
});
