import React, {  useState } from 'react';
import { StyleSheet, TextInput, Image, Button } from 'react-native';

import { Text, View, ImageBackground } from 'react-native';
import { RootTabScreenProps } from '../types';

const TabFourScreen = ({ }: RootTabScreenProps<'TabOne'>) => {
    const image = { uri: "https://doc-08-00-docs.googleusercontent.com/docs/securesc/c0ut33ddjtukav4h7o9iict8mc97d0ou/jqtrr38j3qisfaic5n6ghd9fq56pq0nk/1658002725000/12803465455649165239/12803465455649165239/1UsYhWJ58OkaO1dq7GtCPlgkfZ7KfFIiw?e=view&ax=ACxEAsZM8NME131DXK7s4ALdpqrjhF3mWSLCJeh3psBEE-9AYCssCNImDu8GkQ1rMQUuqPiToFURrXJdr_McrZIBMgcAAddv_OcMz1SKlooKZ96Unh9qL8hlkAQM5G6778x5fiZ9_UFK4KTJkvSi8i2xZtQzKXI7AeaeeOZSB7LPWUqlhDQvByU02RGKqLqQUlkRH0hQUPKQ0HW6JCWoAR1qPhQXUxUsCPu3rFjDFb-uv31Vs-S4XHRVtMTPrw1ley5zaAW7EyuWCaXXMetFoJ4FOiR7N0DB2xQNLW4OP6dLEA2271tfdgODlVFrflIth4yrNFxssq8llcwgxtoI0g8deBiDMUetLFjcis_Y4hXbq-N-NiTSBtRlJrkm2zgo-dZ5D7w_TGTaYMq-WHH2gJHSQxjCagoBD-5UzOoegYz19SPLY6FCFEcWiYkFS1OJU0S9NpxY84I4TTScH3bRDtfF_--8pRke86SNCcDGymeNx5EejFiOxM3QqTLlqfgRxdb2Bjg_vZ7_0QWI0qov0a4RYJ49poL5pw9L8BNjIAVqJoHzJOfojd2nnhz62vwX3Yk5zMUChCkuBz9t55fCls8jefVlzW6HMMIV02TOhxbhsuU7PsNTLR1RPhZmbmN-P7d9Z-hNQrUuGEDMUO293j0Sz9NiQkaSAB0afFNFpBMIYZeclYEc8GgcNsPpDsmdnzjHLhl4fR-sT8uk-S2wosmH0dVglRcnsuZQfcrCd65fyLsRvw-68X1FptWqmkYsgf25uMiFNhoan4t8yHDqO27gVQ&uuid=4b91cdd4-6c1f-470d-b317-19209d714f87&authuser=0&nonce=pluos1qm9nn1i&user=12803465455649165239&hash=apvmafasoe3ed1m6i1q28c79kq446ase" }
    const [isLoading, setLoading] = useState(true)
    const [data, setData] = useState([])
    const [verse, setVerse] = useState('john 3:16')

    const handleSearch = () => {
        fetch(`https://bible-api.com/${verse}`)
            .then((response) => response.json())
            .then((json) => setData(json))
            .catch((error) => console.error(error))
            .finally(() => setLoading(true));
    }

    return (
        <View style={styles.container}>
            <ImageBackground source={image} resizeMode="cover" style={styles.image}></ImageBackground>
            <TextInput
                style={styles.input}
                placeholder="Search By Name....."
                keyboardType='default'
                onChangeText={text => { setVerse(text) }} />
            <Button
                title="Search"
                onPress={handleSearch}

            />

            {isLoading && <View style={{ flex: 1, flexDirection: 'column', justifyContent: 'center' }}>
            <Text style={styles.reference}>{data.reference}</Text>
                <Text style={styles.verse}>{data.text}</Text>
            </View>}

        </View>


    )
}

const styles = StyleSheet.create({
    reference: {
        fontSize: 20,
        textAlign: 'center',
        marginBottom: 10,
        fontWeight: '500'
    },
    verse: {
        fontSize: 16,
        textAlign: 'center'
    },
    image: {
        flex: 1,
        justifyContent: "center"
    },
    input: {
        height: 50,
        width: 350,
        borderWidth: 0.2,
        marginTop: 20,
        borderRadius: 20,
        padding: 10,
        marginBottom: 40,
        marginLeft: 19
    },
    container: {
        flex: 1,
        backgroundColor: 'white'
    }
});

export default TabFourScreen;
